import{addClass}from"./toggleClass.js";const screenInit=(e,t)=>{const n=document.querySelectorAll(".current-score"),r=document.querySelectorAll(".total-score");n.forEach((t=>{t.innerText=e<10?`0${e}`:e})),r.forEach((e=>{e.innerText=t<10?`0${t}`:t}))};export const navInit=()=>{const e=document.querySelectorAll("[data-anchor]"),t=[],n=e.length,r=new IntersectionObserver((e=>{e.forEach((e=>{if(e.isIntersecting){const r=t.indexOf(e.target);screenInit(r+1,n)}const r=e.target.getAttribute("data-anchor");if(e.intersectionRatio>0){document.querySelector(`.navigation a[scroll-href="${r}"]`).classList.add("current")}else document.querySelector(`.navigation a[scroll-href="${r}"]`).classList.remove("current")}))}));e.forEach((e=>{t.push(e),r.observe(e)}))};export const mobNav=e=>{const t=e.length<10?`0${e.length}`:e.length;window.innerWidth<=767&&e.forEach(((e,n)=>{const r=document.createElement("div");addClass(r,"mob-score");const o=n+1<10?`0${n+1}`:n+1;r.innerHTML=`\n    \t\t<span class="current-score">${o}</span> /\n\t\t\t\t<span class="total-score">${t}</span>\n\t\t  `,e.append(r)}))};