export const setContainerWidth=(e,t="centered")=>{const n=e,i=window.innerWidth,d=(i-1200)/2>15?(i-1200)/2:15;if(t)switch(t){case"centered":default:n.style.paddingLeft=d+"px",n.style.paddingRight=d+"px";break;case"right":n.style.paddingLeft=d+"px";break;case"left":n.style.paddingRight=d+"px";case"none":}else console.error("direction is not defined",t)};